import React, { Component } from 'react';

export default class Settings extends Component {
  constructor() {
    super();
    this.state = {
      category: '',
      difficult: '',
      type: '',
    };
  }

  render() {
    const listCategory = [
      { id: 11, name: 'Movies' },
      { id: 20, name: 'Mitology' },
      { id: 9, name: 'General Knowledge' },
      { id: 18, name: 'Computers' },
      { id: 21, name: 'Sports' },
    ];

    const listDifficult = ['easy', 'medium', 'hard'];

    const listType = ['boolean', 'multiple'];

    return (
      <div>
        <h1 data-testid="settings-title">Settings</h1>
        <select>
          {listCategory.map((category) => <option key={ category.id } value={ category.id }>{category.name}</option>)}
        </select>

        <select>
          {listDifficult.map((difficult) => <option key={ difficult } value={ difficult }>{difficult}</option>)}
        </select>

        <select>
          {listType.map((type) => <option key={ type } value={ type }>{type}</option>)}
        </select>
      </div>
    );
  }
}
