import React, { Component } from 'react';

const INITIAL_STATE = { category: '',
  difficult: '',
  type: '' };
export default class Settings extends Component {
  constructor() {
    super();
    this.state = {
      ...INITIAL_STATE,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target: { name, value } }) {
    this.setState({ [name]: value });
  }

  render() {
    const listCategory = [
      { id: 0, name: 'Any Category' },
      { id: 11, name: 'Movies' },
      { id: 20, name: 'Mitology' },
      { id: 9, name: 'General Knowledge' },
      { id: 18, name: 'Computers' },
      { id: 21, name: 'Sports' },
    ];

    const listDifficult = ['easy', 'medium', 'hard', 'both'];

    const listType = ['boolean', 'multiple', 'both'];
    return (
      <div>
        <h1 data-testid="settings-title">Settings</h1>
        <select name="category" onChange={ this.handleChange }>
          {listCategory.map((category) => (
            <option key={ category.id } value={ category.id }>{category.name}</option>
          ))}
        </select>

        <select name="difficult" onChange={ this.handleChange }>
          {listDifficult.map((difficult) => (
            <option key={ difficult } value={ difficult }>{difficult}</option>))}
        </select>

        <select name="type" onChange={ this.handleChange }>
          {listType.map((type) => <option key={ type } value={ type }>{type}</option>)}
        </select>
      </div>
    );
  }
}
